import React, { useRef, useEffect } from 'react';
import JsBarcode from 'jsbarcode';

const PriceLabel = ({ product }) => {
  const printRef = useRef(null);
  const barcodeRef = useRef(null);

  useEffect(() => {
    if (barcodeRef.current) {
      JsBarcode(barcodeRef.current, product.barcode || '123456789012', {
        format: 'EAN13',
        displayValue: false,
        width: 1,
        height: 30,
        margin: 0,
      });
    }
  }, [product.barcode]);

  const handlePrint = () => {
    window.print();
  };

  return (
    <>
      <div ref={printRef} className="printable-area bg-white text-black font-sans w-[50mm] h-[25mm] overflow-hidden text-[10px] leading-tight border border-gray-400 p-[2mm]">
        <div className="flex flex-col h-full justify-between">
          <div>
            <h3 className="font-bold text-[11px] leading-snug">Vrindavan Garden Center</h3>
            <p className="text-[9px] leading-none">Hadapsar,Near Marvel Ritz</p>
            <p className="text-[9px] leading-none">Ph: 9767126970</p>
          </div>

          <div>
            <h4 className="font-bold text-[11px] truncate">{product.name}</h4>
            <div className="flex justify-between mt-1 items-center">
              <span className="text-[13px] font-bold">â‚¹{product.price}</span>
              <svg
                ref={barcodeRef}
                className="h-[20px] w-auto"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Optional button for triggering print */}
      <div className="mt-4">
        <button
          onClick={handlePrint}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Print Label
        </button>
      </div>
    </>
  );
};

export default PriceLabel;
