import React from 'react';
import { Sun, Home } from 'lucide-react';

// --- BARCODE COMPONENT (Requires JsBarcode library) ---
// NOTE: Please add the following script tag to your main index.html file:
// <script src="https://cdn.jsdelivr.net/npm/jsbarcode@3.11.5/dist/JsBarcode.all.min.js"></script>
const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = React.useRef(null);
    
    React.useEffect(() => {
        if (canvasRef.current && value) {
            if (window.JsBarcode) {
                try {
                    window.JsBarcode(canvasRef.current, value, {
                        format: "CODE128",
                        lineColor: "#000",
                        width: 1,
                        // --- MODIFIED: Reduced barcode height to fit the label ---
                        height: 25, 
                        displayValue: false,
                        margin: 0,
                    });
                } catch (e) {
                    console.error("JsBarcode failed to render.", e);
                }
            } else {
                console.warn("JsBarcode library not found. Barcode will not be rendered.");
            }
        }
    }, [value]);

    return <canvas ref={canvasRef} />;
};


const PriceLabel = ({ product }) => {
    const printRef = React.useRef();

    if (!product) return null;

    const handlePrint = () => {
        const printableElement = printRef.current;
        if (!printableElement) {
            console.error("Printable area not found!");
            return;
        }

        // --- FIX: Convert canvas to an image before printing for reliability ---
        const elementToPrint = printableElement.cloneNode(true);
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas) {
            const imgDataUrl = canvas.toDataURL('image/png');
            const img = document.createElement('img');
            img.src = imgDataUrl;
            img.className = canvas.className;
            canvas.parentNode.replaceChild(img, canvas);
        }
        // --- END OF FIX ---

        const innerContentToPrint = elementToPrint.innerHTML;
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet => sheet.href ? `<link rel="stylesheet" href="${sheet.href}">` : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`)
            .join('\n');

        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);

        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        iframeDoc.write(`
            <html>
                <head>
                    <title>Print</title>
                    ${styleSheets}
                </head>
                <body>
                    <div class="printable-area bg-white text-black p-1 rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans">
                        ${innerContentToPrint}
                    </div>
                </body>
            </html>
        `);
        iframeDoc.close();

        iframe.onload = function() {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 500);
        };
    };

    const isOutdoor = product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category.toLowerCase().includes('indoor');

    return (
        <div>
            {/* === MODIFIED JSX with smaller text and spacing === */}
            <div ref={printRef} className="printable-area bg-white text-black p-1 rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans">

                {/* --- MODIFIED: Reduced text size and padding --- */}
                <div className="text-center border-b border-dashed border-gray-300 pb-1">
                    <h2 className="font-bold text-sm">Vrindavan Garden Center</h2>
                    {/* Using arbitrary values for very small text */}
                    <p className="text-[7px] leading-tight">Hadapsar,Near Marvel Ritz | Ph: 9767126970</p>
                </div>

                {/* --- MODIFIED: Reduced padding --- */}
                <div className="flex justify-between items-start pt-1">
                    <div className="flex-1 pr-2">
                        {/* --- MODIFIED: Reduced font sizes --- */}
                        <h4 className="font-bold text-base truncate">{product.name}</h4>
                        <p className="text-[8px] text-gray-600 mt-0.5">{product.description}</p>
                    </div>
                    <div className="text-right">
                        {/* --- MODIFIED: Reduced font size --- */}
                        <span className="text-xl font-extrabold tracking-tighter">₹{product.price}</span>
                        <div className="flex justify-end mt-1 space-x-1">
                            {isOutdoor && (
                                <div className="flex items-center text-[8px] bg-yellow-100 text-yellow-800 px-1 rounded">
                                    <Sun size={10} className="mr-0.5"/> Full Sun
                                </div>
                            )}
                            {isIndoor && (
                                <div className="flex items-center text-[8px] bg-blue-100 text-blue-800 px-1 rounded">
                                    <Home size={10} className="mr-0.5"/> Indoor
                                </div>
                            )}
                        </div>
                    </div>
                </div>
                
                {/* --- MODIFIED: Reduced top margin --- */}
                <div className="flex justify-center mt-1">
                    <PrintableBarcodeCanvas value={product.sku} />
                </div>
            </div>

            <div className="flex justify-center mt-6 no-print">
                <button onClick={handlePrint} className="py-2 px-5 bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors">Print Label</button>
            </div>
        </div>
    );
};

export default PriceLabel;
