import React from 'react';
import { Sun, Home } from 'lucide-react';
React.useEffect(() => {
    const canvas = printRef.current?.querySelector('canvas');
    if (canvas && window.JsBarcode && product?.barcodeValue) {
        try {
            window.JsBarcode(canvas, product.barcodeValue, {
                format: "CODE128",
                lineColor: "#000",
                width: 1,
                height: 25,
                displayValue: false,
                margin: 2,
            });
        } catch (err) {
            console.error("Failed to render barcode for preview", err);
        }
    }
}, [product]);


/**
 * A component to display and print a price label for a product.
 * @param {{ product: { name: string, price: number, category?: string, barcodeValue?: string } }} props
 */
const PriceLabel = ({ product }) => {
    const printRef = React.useRef(null);

    // Your printing logic is solid for this use-case.
    // It correctly creates an isolated iframe to print only the label.
    const handlePrint = () => {
        const printableElement = printRef.current;
        if (!printableElement) return;

        // Clone the node to avoid manipulating the original element
        const elementToPrint = printableElement.cloneNode(true);

        // For printing, it's often more reliable to convert canvas to an image
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas && window.JsBarcode) {
            try {
                // We re-run JsBarcode on a new canvas within the clone to get full quality
                const tempCanvas = document.createElement('canvas');
                window.JsBarcode(tempCanvas, product.barcodeValue || '123456789', {
                    format: "CODE128",
                    lineColor: "#000",
                    width: 1,
                    height: 8,
                    displayValue: false,
                    margin: 2,
                });
                const imgDataUrl = tempCanvas.toDataURL('image/png');
                const img = document.createElement('img');
                img.src = imgDataUrl;
                img.style.width = '100%'; // Let the container control the width
                img.style.height = 'auto';
                canvas.parentNode.replaceChild(img, canvas);
            } catch(e) {
                console.error("Failed to generate barcode image for printing.", e);
            }
        }

        const innerContentToPrint = elementToPrint.innerHTML;

        // Gather all stylesheets to ensure the printout is styled correctly
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet =>
                sheet.href
                    ? `<link rel="stylesheet" href="${sheet.href}">`
                    : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`
            )
            .join('\n');

        // Create a hidden iframe to host the content for printing
        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);

        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        // Set up the iframe document with styles and the label's content
        iframeDoc.write(`
            <html>
                <head>
                    <title>Print Label</title>
                    ${styleSheets}
                </head>
                <body>
                    <div class="printable-area">${innerContentToPrint}</div>
                </body>
            </html>
        `);
        iframeDoc.close();

        // Wait for content to load, then trigger print and cleanup
        iframe.onload = function () {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 250); // A short delay can help ensure resources are ready
        };
    };

    if (!product) return null;

    const isOutdoor = product.category?.toLowerCase().includes('outdoor');
    const isIndoor = product.category?.toLowerCase().includes('indoor');

    return (
        <div className="font-sans">
            {/* The visual label component */}
            <div
                ref={printRef}
                className="printable-area bg-white text-black rounded-md w-[45mm] p-[2mm] border border-gray-300 shadow-md flex flex-col"
            >
                {/* === HEADER === */}
                <div className="text-center">
                    <h2 className="font-bold text-[10px] leading-tight tracking-wide">Vrindavan Garden Center</h2>
                    <p className="text-[9px] leading-tight text-gray-600">Ph: 9767126970</p>
                </div>

                {/* === DIVIDER === */}
                <div className="border-t border-black-200 "></div>

                {/* === BODY === */}
                <div className="flex-grow flex flex-col items-center justify-center text-center">
    
    <div className="flex items-center ">
        <h4 className="font-bold text-[12px] leading-tight">{product.name}</h4>
        {isIndoor && (
            <div className="flex items-center gap-x-1 text-[9px] bg-green-100 text-green-800 px-1.5 py-0.5 rounded-full">
                <Home size={12} />
            </div>
        )}
        {isOutdoor && (
            <div className="flex items-center gap-x-1 text-[9px] bg-orange-100 text-orange-800 px-0.5 py-0.5 rounded-full">
                <Sun size={12} />
            </div>
        )}
    </div>
</div>


                {/* === FOOTER (Price & Barcode) === */}
                
                <div className="mt-auto pt-[2mm] text-center">
                    
                     {product.price && (
                        <p className="font-black text-[10px] leading-none tracking-tighter mb-[1mm]">
                            MRP : â‚¹{product.price}
                        </p>
                    )}
                    {/* Barcode will be rendered here. Using a placeholder canvas. */}
                    <div className="flex justify-center">
                     <canvas className="h-[35px] w-[100px]  flex items-center" />
                        </div>

                </div>

            </div>

            {/* "No-print" wrapper ensures this button doesn't show up on the printout */}
            <div className="flex justify-center mt-4 no-print">
                <button
                    onClick={handlePrint}
                    className="py-2 px-4 text-sm bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
                >
                    Print Label
                </button>
            </div>
        </div>
    );
};

export default PriceLabel;
