import React from 'react';
import { Sun, Home } from 'lucide-react';

// --- BARCODE COMPONENT (Requires JsBarcode library) ---
const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = React.useRef(null);
    
    React.useEffect(() => {
        if (canvasRef.current && value) {
            if (window.JsBarcode) {
                try {
                    window.JsBarcode(canvasRef.current, value, {
                        format: "CODE128",
                        lineColor: "#000",
                        // MODIFIED: Slightly wider bars for better scanning
                        width: 1.5,
                        // MODIFIED: Smaller height to fit in the new footer
                        height: 18, 
                        displayValue: false,
                        margin: 0,
                    });
                } catch (e) {
                    console.error("JsBarcode failed to render.", e);
                }
            } else {
                console.warn("JsBarcode library not found. Barcode will not be rendered.");
            }
        }
    }, [value]);

    return <canvas ref={canvasRef} />;
};


const PriceLabel = ({ product }) => {
    const printRef = React.useRef();

    if (!product) return null;

    const handlePrint = () => {
        // This function remains the same, no changes needed here.
        const printableElement = printRef.current;
        if (!printableElement) return;
        const elementToPrint = printableElement.cloneNode(true);
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas) {
            const imgDataUrl = canvas.toDataURL('image/png');
            const img = document.createElement('img');
            img.src = imgDataUrl;
            img.style.height = '18px'; // Maintain image height
            canvas.parentNode.replaceChild(img, canvas);
        }
        const innerContentToPrint = elementToPrint.innerHTML;
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet => sheet.href ? `<link rel="stylesheet" href="${sheet.href}">` : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`)
            .join('\n');
        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);
        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        // The `<html>` and `<body>` need height: 100% for the flex layout to work in the iframe
        iframeDoc.write(`<html><head><title>Print</title>${styleSheets}<style>html, body { height: 100%; margin: 0; padding: 0; }</style></head><body><div class="printable-area bg-white text-black font-sans w-full h-full">${innerContentToPrint}</div></body></html>`);
        iframeDoc.close();
        iframe.onload = function() {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 500);
        };
    };

    const isOutdoor = product.category && product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category && product.category.toLowerCase().includes('indoor');

    return (
        <div>
            {/* === BIG CHANGE: A NEW 3-ZONE FLEXBOX LAYOUT === */}
            {/* This entire structure is new. It uses flexbox to create vertical zones.
              - No more absolute positioning or weird padding.
              - The `h-full` class is crucial for this to work with the print styles.
            */}
            <div ref={printRef} className="printable-area bg-white text-black font-sans w-full h-full flex flex-col p-1 border-2 border-dashed border-gray-400">

                {/* --- ZONE 1: HEADER (Fixed Height) --- */}
                <div className="flex-shrink-0 text-center border-b border-dashed border-gray-300 pb-1">
                    <h2 className="font-bold text-sm leading-tight">Vrindavan Garden Center</h2>
                    <p className="text-[8px] leading-none">Hadapsar,Near Marvel Ritz | Ph: 9767126970</p>
                </div>

                {/* --- ZONE 2: MAIN CONTENT (Expands to fill available space) --- */}
                <div className="flex-grow flex flex-col justify-center items-center">
                    <h4 className="font-bold text-2xl leading-tight truncate">
                        {product.name}
                    </h4>
                    {product.price && (
                        <p className="font-extrabold text-xl leading-none tracking-tighter">
                            ₹{product.price}
                        </p>
                    )}
                </div>

                {/* --- ZONE 3: FOOTER (Fixed Height) --- */}
                <div className="flex-shrink-0 flex justify-between items-center pt-1 border-t border-dashed border-gray-300">
                    {/* Left side of footer: Icons */}
                    <div className="flex items-center gap-x-2">
                        {isOutdoor && (
                            <div className="flex items-center text-[9px] bg-yellow-100 text-yellow-800 px-1 py-0.5 rounded-sm">
                                <Sun size={10} className="mr-0.5"/> Sun
                            </div>
                        )}
                        {isIndoor && (
                            <div className="flex items-center text-[9px] bg-blue-100 text-blue-800 px-1 py-0.5 rounded-sm">
                                <Home size={10} className="mr-0.5"/> Indoor
                            </div>
                        )}
                    </div>
                    {/* Right side of footer: Barcode */}
                    {product.sku && (
                        <div>
                            <PrintableBarcodeCanvas value={product.sku} />
                        </div>
                    )}
                </div>
            </div>

            <div className="flex justify-center mt-6 no-print">
                <button onClick={handlePrint} className="py-2 px-5 bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors">Print Label</button>
            </div>
        </div>
    );
};

export default PriceLabel;
