import React from 'react';
import { Sun, Home } from 'lucide-react';
import { PrintableBarcodeCanvas } from './Barcode';

const PriceLabel = ({ product }) => {
    if (!product) return null;

    const isOutdoor = product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category.toLowerCase().includes('indoor');

    // Adjusted styles for landscape 50x25mm
    const tinyTextStyle = { fontSize: '6px', lineHeight: '1.1' };
    const smallTextStyle = { fontSize: '7px', lineHeight: '1.1' };
    const plantNameStyle = { fontSize: '10px', lineHeight: '1.1' };
    const priceStyle = { fontSize: '14px', lineHeight: '1' };

    return (
        <div>
            {/* Visual preview for 50mm x 25mm */}
            <div 
                className="printable-area bg-white text-black p-1 rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans flex flex-col justify-between" 
                style={{width: '250px', height: '125px'}}
            >
                <div className="flex w-full h-full">
                    {/* Left Column: Info */}
                    <div className="flex-grow flex flex-col justify-between w-3/5 pr-2 border-r border-dashed border-gray-400">
                        <div>
                            <h3 className="font-bold" style={smallTextStyle}>Vrindavan Garden</h3>
                            <p style={tinyTextStyle}>Hadapsar, Ph: 9767126970</p>
                        </div>
                        <div>
                            <h4 className="font-bold uppercase" style={plantNameStyle}>{product.name}</h4>
                            <p className="text-gray-600" style={smallTextStyle}>{product.description}</p>
                        </div>
                    </div>

                    {/* Right Column: Price & Barcode */}
                    <div className="flex-shrink-0 flex flex-col items-center justify-between w-2/5 pl-2">
                        <div className="flex items-center justify-end w-full">
                            {isIndoor && <Home size={12} />}
                            {isOutdoor && <Sun size={12} />}
                        </div>
                        <p className="font-extrabold" style={priceStyle}>â‚¹{product.price}</p>
                        <div className="w-full">
                            <PrintableBarcodeCanvas value={product.sku} />
                        </div>
                    </div>
                </div>
            </div>

            {/* This is the print button outside the printable area */}
            <div className="flex justify-center mt-6 no-print">
                 <button onClick={() => window.print()} className="py-2 px-5 bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors">Print Label</button>
            </div>
        </div>
    );
};

export default PriceLabel;
