import React from 'react';
import { Sun, Home } from 'lucide-react';

// --- BARCODE COMPONENT (Optional, not used with dummy) ---
const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = React.useRef(null);

    React.useEffect(() => {
        if (canvasRef.current && value) {
            if (window.JsBarcode) {
                try {
                    window.JsBarcode(canvasRef.current, value, {
                        format: "CODE128",
                        lineColor: "#000",
                        width: 1.2,
                        height: 20,
                        displayValue: false,
                        margin: 0,
                    });
                } catch (e) {
                    console.error("JsBarcode failed to render.", e);
                }
            } else {
                console.warn("JsBarcode library not found.");
            }
        }
    }, [value]);

    return <canvas ref={canvasRef} style={{ width: '40mm' }} />;
};

const PriceLabel = ({ product }) => {
    const printRef = React.useRef();

    if (!product) return null;

    const handlePrint = () => {
        const printableElement = printRef.current;
        if (!printableElement) return;
        const elementToPrint = printableElement.cloneNode(true);
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas) {
            const imgDataUrl = canvas.toDataURL('image/png');
            const img = document.createElement('img');
            img.src = imgDataUrl;
            img.style.width = '40mm';
            canvas.parentNode.replaceChild(img, canvas);
        }

        const innerContentToPrint = elementToPrint.innerHTML;
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet =>
                sheet.href
                    ? `<link rel="stylesheet" href="${sheet.href}">`
                    : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`
            )
            .join('\n');

        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);
        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        iframeDoc.write(`<html><head><title>Print</title>${styleSheets}</head><body><div class="printable-area bg-white text-black rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans relative">${innerContentToPrint}</div></body></html>`);
        iframeDoc.close();
        iframe.onload = function () {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 500);
        };
    };

    const isOutdoor = product.category && product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category && product.category.toLowerCase().includes('indoor');

    return (
        <div>
            <div
                ref={printRef}
                className="printable-area bg-white text-black rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans relative px-[4mm] py-[3mm] w-[42mm]"
            >
                {/* HEADER */}
                <div className="text-center border-b border-gray-300 pb-1">
                    <h2 className="font-bold text-[12px] leading-tight">Vrindavan Garden Center</h2>
                    {/* <p className="text-[9px] leading-tight">Hadapsar, Near Marvel Ritz</p> */}
                    <p className="text-[9px] leading-tight">Ph: 9767126970</p>
                </div>
            <h2>fgdug</h2>

                {/* PRODUCT NAME */}
                <div className="flex flex-col items-center justify-center gap-y-1.5 mt-1.5 text-center">
                    <div className="flex justify-center items-center gap-x-1 mt-0.5">
                        <h4 className="font-bold text-[11px] leading-none truncate">{product.name}</h4>
                    {isOutdoor && (
                            <div className="flex items-center text-[9px] bg-yellow-100 text-yellow-800 px-1 py-[1px] rounded-sm">
                                <Sun size={15} className="mr-[5px]" /> 
                            </div>
                        )}
                        {isIndoor && (
                            <div className="flex items-center text-[9px] bg-blue-100 text-blue-800 px-1 py-[1px] rounded-sm">
                                <Home size={15} className="mr-[5px]" /> 
                            </div>
                        )}
                        </div>

                    {/* PRICE + TAGS */}
                    <div className="flex justify-center items-center gap-x-1 mt-0.5">
                        {product.price && (
                            <p className="font-extrabold text-[11px] leading-none tracking-tight">
                                ₹{product.price}
                            </p>
                        )}
                        
                    </div>
                </div>

                {/* BARCODE - Dummy SVG */}
                <div className="mt-[2mm] flex justify-center">
                    <svg xmlns="http://www.w3.org/2000/svg" width="40mm" height="6mm" viewBox="0 0 120 20" preserveAspectRatio="xMidYMid meet">
                        <rect x="0" y="0" width="2" height="20" fill="black" />
                        <rect x="4" y="0" width="1" height="20" fill="black" />
                        <rect x="6" y="0" width="3" height="20" fill="black" />
                        <rect x="10" y="0" width="1" height="20" fill="black" />
                        <rect x="12" y="0" width="2" height="20" fill="black" />
                        <rect x="16" y="0" width="1" height="20" fill="black" />
                        <rect x="18" y="0" width="2" height="20" fill="black" />
                        <rect x="22" y="0" width="3" height="20" fill="black" />
                        <rect x="26" y="0" width="1" height="20" fill="black" />
                        <rect x="28" y="0" width="2" height="20" fill="black" />
                        <rect x="32" y="0" width="1" height="20" fill="black" />
                        <rect x="34" y="0" width="3" height="20" fill="black" />
                        <rect x="38" y="0" width="2" height="20" fill="black" />
                        <rect x="42" y="0" width="1" height="20" fill="black" />
                        <rect x="44" y="0" width="2" height="20" fill="black" />
                        <rect x="48" y="0" width="1" height="20" fill="black" />
                    </svg>
                </div>
            </div>

            <div className="flex justify-center mt-4 no-print">
                <button
                    onClick={handlePrint}
                    className="py-1.5 px-4 text-sm bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors"
                >
                    Print Label
                </button>
            </div>
        </div>
    );
};

export default PriceLabel;
