import React from 'react';
import { Sun, Home } from 'lucide-react';
import { PrintableBarcodeCanvas } from './Barcode';

const PriceLabel = ({ product }) => {
    if (!product) return null;

    const isOutdoor = product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category.toLowerCase().includes('indoor');

    // Custom styles for tiny fonts needed for the small label size
    const tinyTextStyle = { fontSize: '6px', lineHeight: '1.2' };
    const smallTextStyle = { fontSize: '7px', lineHeight: '1.2' };
    const plantNameStyle = { fontSize: '10px', lineHeight: '1.1' };
    const priceStyle = { fontSize: '14px', lineHeight: '1' };

    return (
        <div>
            {/* This is the visual preview shown in the modal */}
            <div className="printable-area bg-white text-black p-1 rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans flex flex-col justify-between" style={{width: '125px', height: '250px'}}>
                <div className="text-center border-b border-dashed border-gray-400 pb-1">
                    <h3 className="font-bold text-sm">Vrindavan Garden</h3>
                    <p style={tinyTextStyle}>Hadapsar, Ph: 9767126970</p>
                </div>
                
                <div className="text-center my-1 flex-grow flex flex-col justify-center">
                    <h4 className="font-bold uppercase" style={plantNameStyle}>{product.name}</h4>
                    <p className="font-extrabold" style={priceStyle}>â‚¹{product.price}</p>
                </div>

                <div className="flex items-start justify-between w-full">
                    <p className="w-3/4 text-left" style={smallTextStyle}>{product.description}</p>
                    <div className="w-1/4 flex justify-end">
                        {isIndoor && <Home size={16} />}
                        {isOutdoor && <Sun size={16} />}
                    </div>
                </div>

                <div className="w-full flex justify-center">
                    <PrintableBarcodeCanvas value={product.sku} />
                </div>
            </div>

            {/* This is the print button outside the printable area */}
            <div className="flex justify-center mt-6 no-print">
                 <button onClick={() => window.print()} className="py-2 px-5 bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors">Print Label</button>
            </div>
        </div>
    );
};

export default PriceLabel;
