import React from 'react';
import { Sun, Home } from 'lucide-react';

// --- BARCODE COMPONENT (Requires JsBarcode library) ---
const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = React.useRef(null);
    
    React.useEffect(() => {
        if (canvasRef.current && value) {
            if (window.JsBarcode) {
                try {
                    window.JsBarcode(canvasRef.current, value, {
                        format: "CODE128",
                        lineColor: "#000",
                        width: 1.2,
                        height: 20, 
                        displayValue: false,
                        margin: 0,
                    });
                } catch (e) {
                    console.error("JsBarcode failed to render.", e);
                }
            } else {
                console.warn("JsBarcode library not found. Barcode will not be rendered.");
            }
        }
    }, [value]);

    return <canvas ref={canvasRef} style={{ width: '40mm' }} />;
};


const PriceLabel = ({ product }) => {
    const printRef = React.useRef();

    if (!product) return null;

    const handlePrint = () => {
        const printableElement = printRef.current;
        if (!printableElement) return;
        const elementToPrint = printableElement.cloneNode(true);
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas) {
            const imgDataUrl = canvas.toDataURL('image/png');
            const img = document.createElement('img');
            img.src = imgDataUrl;
            img.style.width = '40mm';
            canvas.parentNode.replaceChild(img, canvas);
        }
        const innerContentToPrint = elementToPrint.innerHTML;
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet => sheet.href ? `<link rel="stylesheet" href="${sheet.href}">` : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`)
            .join('\n');
        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);
        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        iframeDoc.write(`<html><head><title>Print</title>${styleSheets}</head><body><div class="printable-area bg-white text-black rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans relative">${innerContentToPrint}</div></body></html>`);
        iframeDoc.close();
        iframe.onload = function() {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 500);
        };
    };

    const isOutdoor = product.category && product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category && product.category.toLowerCase().includes('indoor');

    return (
        <div>
            <div ref={printRef} className="printable-area bg-white text-black rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans relative pb-[8mm]">

                {/* --- HEADER --- */}
                <div className="text-center border-b border-gray-300">
                    <h2 className="font-bold text-base leading-none">Vrindavan Garden Center</h2>
                    <p className="text-[9px] leading-none pb-1">Hadapsar,Near Marvel Ritz | Ph: 9767126970</p>
                </div>

                {/* --- MAIN CONTENT AREA --- */}
                <div className="flex flex-col items-center justify-center gap-y-1.5 mt-1.5">
                    {/* MODIFIED: Changed leading-tight to leading-none */}
                    <h4 className="text-center font-bold text-base leading-none truncate">
                        {product.name}
                    </h4>

                    {/* Price and Icons on the SAME LINE */}
                    <div className="flex justify-center items-center gap-x-3 ">
                        {product.price && (
                            <p className="font-extrabold text- leading-none tracking-tighter">
                                ₹{product.price}
                            </p>
                        )}
                        {isOutdoor && (
                            <div className="flex items-center text-[12px] bg-yellow-100 text-yellow-800 px-1.5 py-0.5 rounded-sm">
                                <Sun size={11} className="mr-1"/> Full Sun
                            </div>
                        )}
                        {isIndoor && (
                            <div className="flex items-center text-[12px] bg-blue-100 text-blue-800 px-1.5 py-0.5 rounded-sm">
                                <Home size={11} className="mr-1"/> Indoor
                            </div>
                        )}
                    </div>
                </div>

                {/* Barcode (will only appear if SKU exists)
                {product.sku && (
                    <div className="absolute bottom-[3mm] left-0 right-0 flex justify-center ">
                        <PrintableBarcodeCanvas value={product.sku} />
                    </div>
                )} */}
            </div>

            <div className="flex justify-center mt-6 no-print">
                <button onClick={handlePrint} className="py-2 px-5 bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors">Print Label</button>
            </div>
        </div>
    );
};

export default PriceLabel;
