import React from 'react';
import { Sun, Home } from 'lucide-react';

// --- BARCODE COMPONENT (Requires JsBarcode library) ---
const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = React.useRef(null);
    
    React.useEffect(() => {
        if (canvasRef.current && value) {
            if (window.JsBarcode) {
                try {
                    window.JsBarcode(canvasRef.current, value, {
                        format: "CODE128",
                        lineColor: "#000",
                        width: 1.2,
                        height: 20, 
                        displayValue: false,
                        margin: 0,
                    });
                } catch (e) {
                    console.error("JsBarcode failed to render.", e);
                }
            } else {
                console.warn("JsBarcode library not found. Barcode will not be rendered.");
            }
        }
    }, [value]);

    return <canvas ref={canvasRef} style={{ width: '100%', height: 'auto' }} />;
};


const PriceLabel = ({ product }) => {
    const printRef = React.useRef();

    if (!product) return null;

    const handlePrint = () => {
        const printableElement = printRef.current;
        if (!printableElement) return;

        const elementToPrint = printableElement.cloneNode(true);
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas) {
            const imgDataUrl = canvas.toDataURL('image/png');
            const img = document.createElement('img');
            img.src = imgDataUrl;
            img.style.width = '100%';
            img.style.height = 'auto';
            canvas.parentNode.replaceChild(img, canvas);
        }

        const innerContentToPrint = elementToPrint.innerHTML;
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet => sheet.href ? `<link rel="stylesheet" href="${sheet.href}">` : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`)
            .join('\n');

        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);

        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        // --- FIX: Re-added @page styles to control the print size ---
        iframeDoc.write(`
            <html>
                <head>
                    <title>Print</title>
                    ${styleSheets}
                    <style>
                        @page {
                            /* Set the exact size of your sticker paper */
                            size: 50mm 25mm;
                            margin: 0;
                        }
                        html, body {
                            margin: 0;
                            padding: 0;
                            width: 50mm;
                            height: 25mm;
                            box-sizing: border-box;
                        }
                        .print-container {
                            width: 100%;
                            height: 100%;
                            display: flex;
                            justify-content: center;
                            align-items: center;
                        }
                    </style>
                </head>
                <body>
                    <div class="print-container">
                        ${innerContentToPrint}
                    </div>
                </body>
            </html>
        `);
        iframeDoc.close();

        iframe.onload = function() {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 500);
        };
    };

    const isOutdoor = product.category && product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category && product.category.toLowerCase().includes('indoor');

    return (
        <div>
            {/* --- FIX: Using a more robust flexbox layout for printing --- */}
            <div ref={printRef} className="printable-area bg-white text-black w-[50mm] h-[25mm] p-1 flex flex-col font-sans box-border border-2 border-dashed border-gray-400">

                {/* --- HEADER --- */}
                <div className="text-center">
                    <h2 className="font-bold text-[7px] leading-none">Vrindavan Garden Center</h2>
                    <p className="text-[5px] leading-tight">Hadapsar,Near Marvel Ritz | Ph: 9767126970</p>
                </div>

                {/* --- MAIN CONTENT (takes up available space) --- */}
                <div className="flex-grow flex flex-col items-center justify-center">
                    <h5 className="text-center font-bold text-[10px] leading-none truncate">
                        {product.name}
                    </h5>
                    <div className="flex justify-center items-center gap-x-2 mt-0.5">
                        {product.price && (
                            <p className="font-extrabold text-[10px] leading-none tracking-tighter">
                                â‚¹{product.price}
                            </p>
                        )}
                        {isOutdoor && (
                            <div className="flex items-center text-[8px] bg-yellow-100 text-yellow-800 px-1.5 py-0.5 rounded-sm">
                                <Sun size={8} className="mr-1"/> Full Sun
                            </div>
                        )}
                        {isIndoor && (
                            <div className="flex items-center text-[8px] bg-blue-100 text-blue-800 px-1.5 py-0.5 rounded-sm">
                                <Home size={8} className="mr-1"/> Indoor
                            </div>
                        )}
                    </div>
                </div>

                {/* --- FOOTER (Barcode) --- */}
                {product.sku && (
                    <div className="w-full h-[8mm] flex justify-center items-center">
                         <div className="w-[40mm]">
                            <PrintableBarcodeCanvas value={product.sku} />
                        </div>
                    </div>
                )}
            </div>

            <div className="flex justify-center mt-6 no-print">
                <button onClick={handlePrint} className="py-2 px-5 bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors">Print Label</button>
            </div>
        </div>
    );
};

export default PriceLabel;
