import React from 'react';
import { Sun, Home } from 'lucide-react';

// --- BARCODE COMPONENT (Requires JsBarcode library) ---
const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = React.useRef(null);

    React.useEffect(() => {
        if (canvasRef.current && value) {
            if (window.JsBarcode) {
                try {
                    window.JsBarcode(canvasRef.current, value, {
                        format: "CODE128",
                        lineColor: "#000",
                        width: 1,
                        height: 12,
                        displayValue: false,
                        margin: 0,
                    });
                } catch (e) {
                    console.error("JsBarcode failed to render.", e);
                }
            } else {
                console.warn("JsBarcode library not found.");
            }
        }
    }, [value]);

    return <canvas ref={canvasRef} style={{ width: '35mm' }} />;
};

const PriceLabel = ({ product }) => {
    const printRef = React.useRef();

    if (!product) return null;

    const handlePrint = () => {
        const printableElement = printRef.current;
        if (!printableElement) return;

        const elementToPrint = printableElement.cloneNode(true);
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas) {
            const imgDataUrl = canvas.toDataURL('image/png');
            const img = document.createElement('img');
            img.src = imgDataUrl;
            img.style.width = '35mm';
            canvas.parentNode.replaceChild(img, canvas);
        }

        const innerContentToPrint = elementToPrint.innerHTML;
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet =>
                sheet.href
                    ? `<link rel="stylesheet" href="${sheet.href}">`
                    : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`
            )
            .join('\n');

        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);
        const iframeDoc = iframe.contentWindow.document;

        iframeDoc.open();
        iframeDoc.write(`
            <html>
            <head><title>Print</title>${styleSheets}</head>
            <body>
                <div style="width: 50mm; height: 25mm;" class="printable-area bg-white text-black rounded-sm border border-gray-400 font-sans">
                    ${innerContentToPrint}
                </div>
            </body>
            </html>
        `);
        iframeDoc.close();

        iframe.onload = function () {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 500);
        };
    };

    const isOutdoor = product.category?.toLowerCase().includes('outdoor');
    const isIndoor = product.category?.toLowerCase().includes('indoor');

    return (
        <div>
            <div ref={printRef} className="printable-area p-[1mm] text-[7px] leading-none font-sans" style={{ width: '50mm', height: '25mm' }}>
                {/* Header */}
                <div className="text-center border-b border-gray-300 pb-[0.5mm]">
                    <h2 className="font-bold text-[6px]">Vrindavan Garden Center</h2>
                    <p className="text-[5.5px]">Hadapsar, Ph: 9767126970</p>
                </div>

                {/* Product Info */}
                <div className="mt-[0.8mm] flex flex-col items-center gap-y-[0.8mm]">
                    <h4 className="font-bold text-[8.5px] text-center truncate max-w-full">{product.name}</h4>
                    <div className="flex justify-center items-center gap-x-1">
                        {product.price && (
                            <span className="font-bold text-[7.5px] tracking-tight">â‚¹{product.price}</span>
                        )}
                        {isOutdoor && (
                            <span className="flex items-center bg-yellow-100 text-yellow-800 px-[1mm] py-[0.3mm] rounded-sm text-[5.5px]">
                                <Sun size={8} className="mr-[1mm]" />Sun
                            </span>
                        )}
                        {isIndoor && (
                            <span className="flex items-center bg-blue-100 text-blue-800 px-[1mm] py-[0.3mm] rounded-sm text-[5.5px]">
                                <Home size={8} className="mr-[1mm]" />Indoor
                            </span>
                        )}
                    </div>
                </div>

                {/* Barcode */}
                {product.sku && (
                    <div className="absolute bottom-[1mm] left-0 right-0 flex justify-center">
                        <PrintableBarcodeCanvas value={product.sku} />
                    </div>
                )}
            </div>

            <div className="flex justify-center mt-4 no-print">
                <button onClick={handlePrint} className="py-1 px-3 text-xs bg-green-600 text-white rounded hover:bg-green-500">Print</button>
            </div>
        </div>
    );
};

export default PriceLabel;
