import React, { useState } from 'react';
import { MOCK_DATA } from './data/mockData';

import Sidebar from './components/Sidebar';
import Header from './components/Header';
import Notification from './components/Notification';
import Dashboard from './pages/Dashboard';
import Inventory from './pages/Inventory';
import Billing from './pages/Billing';
import GenericEntityPage from './pages/GenericEntityPage';
import LoginPage from './pages/LoginPage';


export default function App() {
    const [user, setUser] = useState(null);
    const [page, setPage] = useState('dashboard');
    const [products, setProducts] = useState(MOCK_DATA.products);
    const [customers, setCustomers] = useState(MOCK_DATA.customers);
    const [notification, setNotification] = useState({ message: '', type: '' });

    const showNotification = (message, type = 'info') => {
        setNotification({ message, type });
    };

    const handleLogin = (userData) => {
        setUser(userData);
    };

    const handleLogout = () => {
        setUser(null);
        setPage('dashboard');
    };

    if (!user) {
        return <LoginPage onLogin={handleLogin} />;
    }

    const renderPage = () => {
        switch (page) {
            case 'dashboard':
                return <Dashboard products={products} />;
            case 'inventory':
                return <Inventory products={products} setProducts={setProducts} />;
            case 'billing':
                return <Billing products={products} customers={customers} setProducts={setProducts} showNotification={showNotification} />;
            case 'customers':
                return <GenericEntityPage 
                    title="Customers"
                    items={customers}
                    setItems={setCustomers}
                    entityName="Customer"
                    formFields={[
                        { name: 'name', type: 'text', placeholder: 'Customer Name' },
                        { name: 'email', type: 'email', placeholder: 'Email Address' },
                        { name: 'phone', type: 'tel', placeholder: 'Phone Number' },
                    ]}
                />;
            default:
                return <Dashboard products={products} />;
        }
    };

    return (
        <div className="flex h-screen bg-gray-900 text-white font-sans">
            <Notification message={notification.message} type={notification.type} onClose={() => setNotification({ message: '', type: '' })} />
            <Sidebar user={user} onNavigate={setPage} activePage={page} />
            <div className="flex-1 flex flex-col overflow-hidden">
                <Header user={user} onLogout={handleLogout} />
                <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-900">
                    {renderPage()}
                </main>
            </div>
        </div>
    );
}
