import React from 'react';
import { Sun, Home } from 'lucide-react';

// --- ADDED BARCODE COMPONENT TO FIX RESOLUTION ERROR ---
// NOTE: This component requires the JsBarcode library.
// Please add the following script tag to your main index.html file:
// <script src="https://cdn.jsdelivr.net/npm/jsbarcode@3.11.5/dist/JsBarcode.all.min.js"></script>
const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = React.useRef(null);

    React.useEffect(() => {
        // Ensure the canvas ref and value exist
        if (canvasRef.current && value) {
            // Check if the JsBarcode library is available on the window object
            if (window.JsBarcode) {
                try {
                    window.JsBarcode(canvasRef.current, value, {
                        format: "CODE128",
                        lineColor: "#000",
                        width: 2,
                        height: 40,
                        displayValue: false, // The value is usually printed separately
                        margin: 0,
                    });
                } catch (e) {
                    // Log any errors from JsBarcode
                    console.error("JsBarcode failed to render.", e);
                }
            } else {
                // Warn the developer if the library is missing
                console.warn("JsBarcode library not found. Barcode will not be rendered.");
            }
        }
    }, [value]); // Rerun this effect if the barcode value changes

    // Render a canvas element that JsBarcode will use to draw the barcode
    return <canvas ref={canvasRef} />;
};


const PriceLabel = ({ product }) => {
    // The ref will now be attached directly to the printable div
    const printRef = React.useRef();

    if (!product) return null;

    const handlePrint = () => {
        const printableElement = printRef.current;
        if (!printableElement) {
            console.error("Printable area not found!");
            return;
        }

        // --- FIX: Convert canvas to an image before printing ---
        const elementToPrint = printableElement.cloneNode(true);
        const canvas = elementToPrint.querySelector('canvas');
        if (canvas) {
            const imgDataUrl = canvas.toDataURL('image/png');
            const img = document.createElement('img');
            img.src = imgDataUrl;
            img.className = canvas.className;
            canvas.parentNode.replaceChild(img, canvas);
        }
        // --- END OF FIX ---

        const innerContentToPrint = elementToPrint.innerHTML;
        const styleSheets = Array.from(document.styleSheets)
            .map(sheet => sheet.href ? `<link rel="stylesheet" href="${sheet.href}">` : `<style>${Array.from(sheet.cssRules).map(rule => rule.cssText).join('')}</style>`)
            .join('\n');

        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        document.body.appendChild(iframe);

        const iframeDoc = iframe.contentWindow.document;
        iframeDoc.open();
        // --- FIX: Added specific print styles to control page size ---
        iframeDoc.write(`
            <html>
                <head>
                    <title>Print</title>
                    ${styleSheets}
                    <style>
                        /* Define the exact size of your sticker paper */
                        @page {
                            /* Common label size, adjust if needed */
                            size: 4in 3in; 
                            margin: 0;
                        }
                        html, body {
                            margin: 0;
                            padding: 0;
                            width: 4in; /* Must match @page size */
                            height: 3in; /* Must match @page size */
                        }
                        /* This container ensures the content is centered and fits */
                        .print-container {
                            width: 100%;
                            height: 100%;
                            display: flex;
                            justify-content: center;
                            align-items: center;
                            overflow: hidden;
                        }
                    </style>
                </head>
                <body>
                    <!-- Use the print-container to enforce the size -->
                    <div class="print-container">
                        <div class="printable-area bg-white text-black p-4 rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans">
                            ${innerContentToPrint}
                        </div>
                    </div>
                </body>
            </html>
        `);
        // --- END OF FIX ---
        iframeDoc.close();

        iframe.onload = function() {
            setTimeout(() => {
                iframe.contentWindow.focus();
                iframe.contentWindow.print();
                document.body.removeChild(iframe);
            }, 500);
        };
    };

    const isOutdoor = product.category.toLowerCase().includes('outdoor');
    const isIndoor = product.category.toLowerCase().includes('indoor');

    return (
        <div>
            <div ref={printRef} className="printable-area bg-white text-black p-4 rounded-lg max-w-xs mx-auto border-2 border-dashed border-gray-400 font-sans">
                <div className="text-center border-b-2 border-dashed border-gray-300 pb-2">
                    <h3 className="font-bold text-lg">Vrindavan Garden Center</h3>
                    <p className="text-xs">Hadapsar,Near Marvel Ritz | Ph: 9767126970</p>
                </div>
                <div className="flex justify-between items-start pt-2">
                    <div className="flex-1 pr-2">
                        <h4 className="font-bold text-2xl truncate">{product.name}</h4>
                        <p className="text-xs text-gray-600 mt-1">{product.description}</p>
                    </div>
                    <div className="text-right">
                        <span className="text-3xl font-extrabold tracking-tighter">₹{product.price}</span>
                        <div className="flex justify-end mt-2">
                            {isOutdoor && (
                                <div className="flex items-center text-xs bg-yellow-100 text-yellow-800 p-1 rounded">
                                    <Sun size={14} className="mr-1"/> Full Sun
                                </div>
                            )}
                            {isIndoor && (
                                <div className="flex items-center text-xs bg-blue-100 text-blue-800 p-1 rounded">
                                    <Home size={14} className="mr-1"/> Indoor
                                </div>
                            )}
                        </div>
                    </div>
                </div>
                <div className="flex justify-center mt-2">
                    <PrintableBarcodeCanvas value={product.sku} />
                </div>
            </div>

            <div className="flex justify-center mt-6 no-print">
                <button onClick={handlePrint} className="py-2 px-5 bg-green-600 text-white rounded-lg hover:bg-green-500 transition-colors">Print Label</button>
            </div>
        </div>
    );
};

export default PriceLabel;
