import React, { useEffect, useRef } from 'react';

const BarcodeCanvas = ({ value }) => {
    const canvasRef = useRef(null);

    useEffect(() => {
        if (canvasRef.current && typeof window.JsBarcode !== 'undefined') {
            try {
                window.JsBarcode(canvasRef.current, value, {
                    format: 'CODE128',
                    lineColor: '#ffffff',
                    background: '#2d3748',
                    width: 2,
                    height: 50,
                    displayValue: true,
                    fontOptions: "bold",
                    font: "Inter",
                    fontSize: 16,
                    textColor: "#ffffff",
                    margin: 10
                });
            } catch (e) {
                console.error("Barcode generation failed:", e);
            }
        } else if (canvasRef.current) {
            console.error("JsBarcode library not found on window object.");
            const ctx = canvasRef.current.getContext('2d');
            ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
            ctx.fillStyle = '#ef4444';
            ctx.font = '12px Inter';
            ctx.textAlign = 'center';
            ctx.fillText('Barcode library could not be loaded.', canvasRef.current.width / 2, canvasRef.current.height / 2);
        }
    }, [value]);

    return <canvas ref={canvasRef} />;
};

const PrintableBarcodeCanvas = ({ value }) => {
    const canvasRef = useRef(null);

    useEffect(() => {
        if (canvasRef.current && typeof window.JsBarcode !== 'undefined') {
            try {
                window.JsBarcode(canvasRef.current, value, {
                    format: 'CODE128',
                    lineColor: '#000000',
                    background: '#ffffff',
                    width: 1.5,
                    height: 40,
                    displayValue: false,
                    margin: 10
                });
            } catch (e) {
                console.error("Printable Barcode generation failed:", e);
            }
        }
    }, [value]);

    return <canvas ref={canvasRef} />;
};


export { BarcodeCanvas, PrintableBarcodeCanvas };
